import React from "react";
import { HorizontalBar } from "react-chartjs-2";


const ClientDataChart = (props) => {
  const {

    clientsOnboarded,
    clientsOffboarded,
    clientsOnboardedFiles,
    clientsOffboardedFiles,
  } = props.data;

  const dataStructure = {
    "Clients Onboarded": clientsOnboarded,
    "Clients Offboarded": clientsOffboarded,
    "Clients Onboarded Files": clientsOnboardedFiles,
    "Clients Offboarded Files": clientsOffboardedFiles,
  };

  const chartDataConfig = {
    labels: Object.keys(dataStructure),
    datasets: [{
      data: Object.values(dataStructure),
      label: "Total",
      backgroundColor: "rgba(255,99,132,0.2)",
      borderColor: "rgba(255,99,132,1)",
      borderWidth: 1,
      hoverBackgroundColor: "rgba(255,99,132,0.4)",
      hoverBorderColor: "rgba(255,99,132,1)",
    },

    ],

  };
  console.log(props.data)
  return (

    <div>

      <select onChange={(e) => props.setQuery(e.target.value)} value={props.activeQuery}>
        <option value="2020">2020</option>
        <option value="2019">2019</option>
        <option value="2018">2018</option>
        <option value="2017">2017</option>
        <option value="2016">2016</option>

      </select>

      <HorizontalBar data={chartDataConfig} />

    </div>
  );

};

export default ClientDataChart;
