import React, { useState, useEffect } from 'react'
import ClientDataChart from './ClientDataChart'


const Chart = (props) => {
  const [activeQuery, setActiveQuery] = useState("2020");
  const [clientData, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('')


  useEffect(() => {
    fetch(`http://localhost:5000/client/${activeQuery}`)
      .then(res => res.json())
      .then(data => {
        setData(data);
        setLoading(false);
      })
      .catch((error) => setError(error));
  }, [activeQuery]);

  return (

    <div className="chart">
      {loading && <p>Chart loading...</p>}
      {error && <p>{error.message}</p>}

      <Bar
        data={data}
        options={{
          title: {
            display: true,
            text: "Andalucía population by region",
            fontSize: 25
          },
          legend: {
            display: true,
            position: 'right'
          }
        }}
      />
    </div>
  )
}

export default Chart;
