import React, { useState, useEffect } from 'react'
import { Switch, Select } from '@material-ui/core';
import { HorizontalBar } from 'react-chartjs-2';

const CountryChart = (props) => {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  const [country, setCountry] = useState([]);
  const [year, setYear] = useState([])

  useEffect(() => {
    fetch(
      'http://localhost:5000/countries')
      .then(res => res.json())
      .then(data => {
        data.map((item) => {
          setYear(item.year)
          setCountry(item.country)
        })


        let chartData = {
          labels: data.map(({ year }) => year),
          datasets: [{
            label: 'Total population',
            data: data.map(({ value }) => value),
            backgroundColor: [
              'rgb(50, 168, 82)',
              'rgb(168, 50, 50)',
              'rgb(50, 144, 168)',
              'rgb(142, 50, 168)',
              'rgb(193, 201, 34)',
              'rgb(64, 237, 231)',
              'rgb(237, 64, 64)',
              'rgb(32, 103, 135)']
          },
          {
            label: 'country',
            data: data.map(({ country }) => country),
            backgroundColor: [
              'rgb(65, 204, 103)',
              'rgb(222, 73, 73)',
              'rgb(78, 193, 222)',
              'rgb(190, 70, 224)',
              'rgb(219, 227, 68)',
              'rgb(111, 227, 223)',
              'rgb(237, 114, 114)',
              'rgb(60, 158, 201)']
          }

          ]
        }
        setData(chartData);
        setLoading(false)
      })
      .catch(error => console.log(error));
  }, []);

  return (

    <div className="chart">
      {loading && <p>Chart loading...</p>}
      <Switch name="checkedA" onClick={props.handleClick} />
      <Select labelId="demo-simple-select-label"
        id="demo-simple-select"
        value={year}
        onChange={handleChange}></Select>
      <HorizontalBar
        data={data}
        options={{
          title: {
            display: true,
            text: "World population by year",
            fontSize: 25
          },
          legend: {
            display: true,
            position: 'right'
          }
        }}
      />
    </div>
  )
}

export default CountryChart;
