import React from 'react'
import { Switch, FormGroup, FormControlLabel } from '@material-ui/core';
import { Bar } from 'react-chartjs-2';

class Chart extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      dataSet: [],
      isLoading: true,
      chartData: {
        labels: this.state.dataSet.map(({ city }) => city),
        datasets: [{
          label: 'Total population',
          data: this.state.dataSet.map(({ total }) => total),
          backgroundColor: [
            'rgb(50, 168, 82)',
            'rgb(168, 50, 50)',
            'rgb(50, 144, 168)',
            'rgb(142, 50, 168)',
            'rgb(193, 201, 34)',
            'rgb(64, 237, 231)',
            'rgb(237, 64, 64)',
            'rgb(32, 103, 135)']
        },
        {
          label: 'Men',
          data: this.state.dataSet.map(({ men }) => men),
          backgroundColor: [
            'rgb(65, 204, 103)',
            'rgb(222, 73, 73)',
            'rgb(78, 193, 222)',
            'rgb(190, 70, 224)',
            'rgb(219, 227, 68)',
            'rgb(111, 227, 223)',
            'rgb(237, 114, 114)',
            'rgb(60, 158, 201)']
        },
        {
          label: 'Women',
          data: this.state.dataSet.map(({ women }) => women),
          backgroundColor: [
            'rgba(111, 227, 143)',
            'rgba(240, 115, 115)',
            'rgba(140, 218, 237)',
            'rgba(211, 127, 235)',
            'rgba(234, 240, 120)',
            'rgba(182, 227, 225)',
            'rgba(237, 164, 164)',
            'rgb(106, 196, 235)']
        }
        ]

      }
    }
  }

  componentDidMount() {
    fetch('http://localhost:5000')
      .then(response => response.json())
      .then(data => this.setState({ dataSet: data, isLoading: true }));
  }
  render() {
    console.log(this.state.dataSet)
    return (
      <div className="chart">

        <Switch name="checkedA" onClick={this.props.handleClick} />

        {this.state.isLoading ? (<Bar
          data={this.state.chartData}

          options={{
            title: {
              display: true,
              text: "Andalucía population by region",
              fontSize: 25
            },
            legend: {
              display: true,
              position: 'right'
            }
          }}
        />) : (<div>Loading ....</div>)}
      </div>

    )
  }

}

export default Chart;
