import React from 'react';
import ReactApexChart from "react-apexcharts";
import dataProcess from './data';
const HeatMap = (props) => {

  console.log(dataProcess)
  const dataSeries = [
    {
      name: 'Metric1',
      data: [50, 60, 80, 90]
    },
    {
      name: 'Metric2',
      data: [100, 200, 300, 400]
    },
    {
      name: 'Metric3',
      data: [100, 200, 300, 400]
    },
    {
      name: 'Metric4',
      data: [100, 200, 300, 400]
    },
    {
      name: 'Metric5',
      data: [100, 200, 300, 400]
    },
    {
      name: 'Metric6',
      data: [100, 200, 300, 400]
    },
    {
      name: 'Metric7',
      data: [100, 200, 300, 400]
    },
    {
      name: 'Metric8',
      data: [100, 200, 300, 400]
    },
    {
      name: 'Metric9',
      data: [100, 200, 300, 400]
    }

  ]


  const dataOptions = {
    series: [{
      name: "carmen",
      data: [{ x: "y", y: "z" }]
    }],
    chart: {
      toolbar: {
        show: false,
      }
    },
    dataLabels: {
      enabled: true
    },
    colors: ["#2B47D2"]
  };
  const [xAxis, setXAxis] = React.useState([]);
  const [yAxis, setYAxis] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [customerData, setCustomerData] = React.useState([])
  React.useEffect(() => {

    setCustomerData(dataProcess)
    setLoading(false);

  }, []);
  return (

    <div style={{ padding: "40px" }} >
      <div className="mixed-chart" style={{ width: "60%", margin: "auto" }}>
        <h1>Client Processes</h1>
        {/* <ReactApexChart
          options={chartOptions}
          series={chartSeries}
          type="heatmap"

        /> */}
      </div>
    </div >

  );
}


export default HeatMap;
