import React, { useState, useEffect } from 'react'
import { Switch, Select } from '@material-ui/core';
import { HorizontalBar } from 'react-chartjs-2';

const CountryChart = (props) => {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  const [year, setYear] = useState([]);

  useEffect(() => {
    fetch(
      'http://localhost:5000/clients')
      .then(res => res.json())
      .then(data => {
        console.log(data)

        let chartData = {
          labels: data.map(({ year }) => year),
          datasets: [{
            label: 'Onboarded BRs',
            data: data.map(({ onboardedBRs }) => onboardedBRs),
            backgroundColor: [
              'rgb(50, 168, 82)',
              'rgb(168, 50, 50)',
              'rgb(50, 144, 168)',
              'rgb(142, 50, 168)',
              'rgb(193, 201, 34)'
            ]
          },
          {
            label: 'Offboarded BRS',
            data: data.map(({ offboardedBRS }) => offboardedBRS),
            backgroundColor: [
              'rgb(65, 204, 103)',
              'rgb(222, 73, 73)',
              'rgb(78, 193, 222)',
              'rgb(190, 70, 224)',
              'rgb(219, 227, 68)'
            ]
          },
          {
            label: 'Onboarded Files',
            data: data.map(({ onboardedFiles }) => onboardedFiles),
            backgroundColor: [
              'rgba(111, 227, 143)',
              'rgba(240, 115, 115)',
              'rgba(140, 218, 237)',
              'rgba(211, 127, 235)',
              'rgba(234, 240, 120)'
            ]
          },
          {
            label: 'Offboarded Files',
            data: data.map(({ offboardedFiles }) => offboardedFiles),
            backgroundColor: [
              'rgb(65, 204, 150)',
              'rgb(222, 73, 73)',
              'rgb(78, 193, 222)',
              'rgb(190, 70, 224)',
              'rgb(219, 227, 68)'
            ]
          }


          ]
        }
        setData(chartData);
        setLoading(false)
      })
      .catch(error => console.log(error));
  }, []);

  return (

    <div className="chart">
      {loading && <p>Chart loading...</p>}
      <Switch name="checkedA" onClick={props.handleClick} />
      <Select labelId="demo-simple-select-label"
        id="demo-simple-select"></Select>
      <HorizontalBar
        data={data}
        options={{
          title: {
            display: true,
            text: "Client Information",
            fontSize: 25
          },
          legend: {
            display: false,
            position: 'right'
          },
          animation: {
            duration: 2000,
            onComplete: function () {
              console.log(legend)
            }
          }
        }}
      />
    </div>
  )
}

export default CountryChart;
