import React from 'react';
import ReactApexChart from "react-apexcharts";
import dataProcess from './data';
const HeatMap = (props) => {

  const [chartOptions, setOptions] = React.useState({});
  const [chartSeries, getSeries] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [activeQuery, setActiveQuery] = React.useState('EMEA')
  const [customerData, setCustomerData] = React.useState([])
  const [dataByRegion, setDataByRegion] = React.useState([])
  const systems = dataProcess.map((item) => item['Closure Systems'].map((system) => system['System Name']));

  console.log(systems)
  const dataOptions = {
    chart: {
      toolbar: {
        show: false,
      }
    },
    tooltip: {
      y: {

        title: {
          formatter: (seriesName) =>
            dataProcess.map((item) => item['Closure Systems'].map((system) => seriesName = system['System Name']))
        }
      }

    },
    plotOptions: {
      heatmap: {
        radius: 4,
        colorScale: {
          ranges: [{
            from: 0,
            to: 2000,
            color: "#4bb0ff",
            name: "0 - 2000"
          },
          {
            from: 2001,
            to: 4000,
            color: "#0063b0",
            name: "2001 - 4000"
          },
          {
            from: 4001,
            to: 6000,
            color: "#003863",
            name: "4001 - 6000"
          }
          ]
        }

      }
    },
    xaxis: {
      categories: dataProcess.map((item) => item["Location code"])
    },
    dataLabels: {
      enabled: true,
      // style: {
      //   colors: ["#787878"],
      //   fontWeight: "lighter"
      // },
    },
    title: {
      text: 'Method',
      margin: 10,
      offsetX: 40,
      offsetY: 10,
      floating: true,
      style: {
        fontSize: '16px',

      },

    },

    colors: ["#008FFB"],
  };
  const dataSeries = [{
    name: "Manual Closures",
    data: dataProcess.map((item) => item["Manual Closures"])
  },
  {
    name: "RPA Closures",
    data: dataProcess.map((item) => item["RPA Closures"])
  },
  {
    name: "Closures with script available",
    data: dataProcess.map((item) => item["Closures with script available"])
  }]
  React.useEffect(() => {

    setOptions(dataOptions);
    getSeries(dataSeries)
    setLoading(false);

  }, []);


  React.useEffect(() => {

    const search = (activeQuery, dataOptions) => {

      for (var i = 0; i < dataOptions.length; i++) {
        if (dataOptions[i].year === activeQuery) {
          setDataByRegion(dataOptions[i]);
        }

      }
    }

    search(activeQuery, dataOptions)
  }, [activeQuery]);

  return (

    <div style={{ padding: "40px" }} >
      {loading && <p>Chart loading...</p>}
      <div className="mixed-chart" style={{ margin: "auto" }}>
        <h1>Offboarding Activities</h1>
        <ReactApexChart
          options={chartOptions}
          series={chartSeries}
          type="heatmap"
          height="300px"

        />
        <p>Country Code</p>
      </div>
    </div >

  );
}


export default HeatMap;
