import React, { useState, useEffect } from 'react'
import ClientDataChart from './ClientDataChart'


const Chart = (props) => {
  const [activeQuery, setActiveQuery] = useState("2020");
  const [clientData, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false)


  useEffect(() => {
    fetch(
      'http://localhost:5000')
      .then(res => res.json())
      .then(data => {
        let chartData = {
          labels: data.map(({ city }) => city),
          datasets: [{
            label: 'Total population',
            data: data.map(({ total }) => total),
            backgroundColor: [
              'rgb(50, 168, 82)',
              'rgb(168, 50, 50)',
              'rgb(50, 144, 168)',
              'rgb(142, 50, 168)',
              'rgb(193, 201, 34)',
              'rgb(64, 237, 231)',
              'rgb(237, 64, 64)',
              'rgb(32, 103, 135)']
          },
          {
            label: 'Men',
            data: data.map(({ men }) => men),
            backgroundColor: [
              'rgb(65, 204, 103)',
              'rgb(222, 73, 73)',
              'rgb(78, 193, 222)',
              'rgb(190, 70, 224)',
              'rgb(219, 227, 68)',
              'rgb(111, 227, 223)',
              'rgb(237, 114, 114)',
              'rgb(60, 158, 201)']
          },
          {
            label: 'Women',
            data: data.map(({ women }) => women),
            backgroundColor: [
              'rgba(111, 227, 143)',
              'rgba(240, 115, 115)',
              'rgba(140, 218, 237)',
              'rgba(211, 127, 235)',
              'rgba(234, 240, 120)',
              'rgba(182, 227, 225)',
              'rgba(237, 164, 164)',
              'rgb(106, 196, 235)']
          }
          ]
        }
        setData(chartData);
        setLoading(false)
      })
      .catch(error => console.log(error));
  }, []);

  return (

    <div className="chart">
      {loading && <p>Chart loading...</p>}
      <Switch name="checkedA" onClick={props.handleClick} />

      <Bar
        data={data}
        options={{
          title: {
            display: true,
            text: "Andalucía population by region",
            fontSize: 25
          },
          legend: {
            display: true,
            position: 'right'
          }
        }}
      />
    </div>
  )
}

export default Chart;
