    "women": "383056",
    "total": "753424"
  },
  {
    "city": "Granada",
    "men": "398148",
    "women": "409195",
    "total": "807343"
  },
  {
    "city": "Huelva",
    "men": "221675",
    "women": "228749",
    "total": "450424"
  },
  {
    "city": "Jaén",
    "men": "308688",
    "women": "318583",
    "total": "627271"
  },
  {
    "city": "Málaga",
    "men": "558939",
    "women": "567819",
    "total": "1126758"
  },
  {
    "city": "Sevilla",
    "men": "831409",
    "women": "848402",
    "total": "1679811"
  }

]


const labels = data.map(({ city }) => city)
const men = data.map(({ men }) => men)
const women = data.map(({ women }) => women)
const total = data.map(({ total }) => total)
console.log(men)
class Chart extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      chartData: {
        labels: labels,
        datasets: [{
          label: 'Total population',
          data: total,
          backgroundColor: [
            'rgb(50, 168, 82)',
            'rgb(168, 50, 50)',
            'rgb(50, 144, 168)',
            'rgb(142, 50, 168)',
            'rgb(193, 201, 34)',
            'rgb(64, 237, 231)',
            'rgb(237, 64, 64)',
            'rgb(32, 103, 135)']
        },
        {
          label: 'Men',
          data: men,
          backgroundColor: [
            'rgb(65, 204, 103)',
            'rgb(222, 73, 73)',
            'rgb(78, 193, 222)',
            'rgb(190, 70, 224)',
            'rgb(219, 227, 68)',
            'rgb(111, 227, 223)',
            'rgb(237, 114, 114)',
            'rgb(60, 158, 201)']
        },
        {
          label: 'Women',
          data: women,
          backgroundColor: [
            'rgba(111, 227, 143)',
            'rgba(240, 115, 115)',
            'rgba(140, 218, 237)',
            'rgba(211, 127, 235)',
            'rgba(234, 240, 120)',
            'rgba(182, 227, 225)',
            'rgba(237, 164, 164)',
            'rgb(106, 196, 235)']
        }
        ]

      }
    }
  }

  componentDidMount() {
    fetch(API + DEFAULT_QUERY)
      .then(response => response.json())
      .then(data => this.setState({ chartData: data }));
  }
  render() {

    return (
      <div className="chart">

        <Switch name="checkedA" onClick={this.props.handleClick} />

        <Bar
          data={this.state.chartData}

          options={{
            title: {
              display: true,
              text: "Andalucía population by region",
              fontSize: 25
            },
            legend: {
              display: true,
              position: 'right'
            }
          }}
        />
      </div>

    )
  }

}

export default Chart;
