import React, { useEffect, useState } from 'react';
import { Line } from "react-chartjs-2";
import burndownData from "./burndownData";

const AutomationRealTimeChart = () => {

  const [automationData, setAutomationData] = useState([]);
  const [dayCounter, setDayCounter] = useState(0);
  const [initialState, setInitialState] = useState([]);
  const [chartData, setChartData] = useState([]);
  const chartOptions = {
    labels: automationData.map((item) => item.Day),
    datasets: [
      {
        data: automationData.map((item) => item["Current state"]),
        label: "Current State",
        fill: false,
        lineTension: 0.1,
        backgroundColor: "rgba(75,192,192,0.4)",
        borderColor: "rgba(75,192,192,1)",
        borderCapStyle: "butt",
        borderDash: [],
        borderDashOffset: 0.0,
        borderJoinStyle: "miter",
        pointBorderColor: "rgba(75,192,192,1)",
        pointBackgroundColor: "#fff",
        pointBorderWidth: 1,
        pointHoverRadius: 5,
        pointHoverBackgroundColor: "rgba(75,192,192,1)",
        pointHoverBorderColor: "rgba(220,220,220,1)",
        pointHoverBorderWidth: 2,
        pointRadius: 1,
        pointHitRadius: 10,

      },
      {
        data: automationData.map((item) => item["Improved State"]),
        label: "Improved State",
        fill: false,
        lineTension: 0.1,
        backgroundColor: "rgba(255, 0, 0, 0.6)",
        borderColor: "rgba(255, 0, 0, 1)",
        borderCapStyle: "butt",
        borderDash: [],
        borderDashOffset: 0.0,
        borderJoinStyle: "miter",
        pointBorderColor: "rgba(255, 0, 0, 1)",
        pointBackgroundColor: "#fff",
        pointBorderWidth: 1,
        pointHoverRadius: 5,
        pointHoverBackgroundColor: "rgba(255, 0, 0, 1)",
        pointHoverBorderColor: "rgba(220,220,220,1)",
        pointHoverBorderWidth: 2,
        pointRadius: 1,
        pointHitRadius: 10,

      },
      {
        data: automationData.map((item) => item["Optimal State"]),
        label: "Optimal State",
        fill: false,
        lineTension: 0.1,
        backgroundColor: "rgb(241, 226, 95, 0.4)",
        borderColor: "rgb(241, 226, 95, 1)",
        borderCapStyle: "butt",
        borderDash: [],
        borderDashOffset: 0.0,
        borderJoinStyle: "miter",
        pointBorderColor: "rgb(241, 226, 95, 1)",
        pointBackgroundColor: "#fff",
        pointBorderWidth: 1,
        pointHoverRadius: 5,
        pointHoverBackgroundColor: "rgb(241, 226, 95, 1)",
        pointHoverBorderColor: "rgba(220,220,220,1)",
        pointHoverBorderWidth: 2,
        pointRadius: 1,
        pointHitRadius: 10,

      },
    ],

  }
  useEffect(() => {
    setChartData(chartOptions)
    if (dayCounter < 50) {
      setTimeout(() => {
        setDayCounter(dayCounter + 1);
        setAutomationData(() => burndownData.slice(0, dayCounter))

      }, 1000)
    }

  }, [dayCounter])


  return (
    <div style={{ width: "80%", margin: "40px auto" }} id="chart">
      <Line redraw
        data={chartData}
        options={{
          scales: {
            xAxes: [{
              ticks: {
                pointHitRadius: 4
              }
            }]
          },
          animation: {
            duration: 0
          }
        }}

      />
    </div>
  );
};

export default AutomationRealTimeChart;
