import React from 'react';
import ReactApexChart from "react-apexcharts";

const HeatMap = (props) => {
  const dataSeries = [
    {
      name: 'Metric1',
      data: [50, 60, 80, 90]
    },
    {
      name: 'Metric2',
      data: [100, 200, 300, 400]
    },
    {
      name: 'Metric3',
      data: [100, 200, 300, 400]
    },
    {
      name: 'Metric4',
      data: [100, 200, 300, 400]
    },
    {
      name: 'Metric5',
      data: [100, 200, 300, 400]
    },
    {
      name: 'Metric6',
      data: [100, 200, 300, 400]
    },
    {
      name: 'Metric7',
      data: [100, 200, 300, 400]
    },
    {
      name: 'Metric8',
      data: [100, 200, 300, 400]
    },
    {
      name: 'Metric9',
      data: [100, 200, 300, 400]
    }

  ]

  const dataOptions = {
    chart: {
      type: 'heatmap',
    },
    dataLabels: {
      enabled: true
    },
    colors: ["#2B47D2"],
    title: {
      text: 'Client Processes'
    },
  };
  const [chartOptions, setOptions] = React.useState({});
  const [chartSeries, getSeries] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  React.useEffect(() => {

    setOptions(dataOptions);
    getSeries(dataSeries)
    setLoading(false);

  }, []);
  console.log(dataOptions)
  return (

    <div style={{ textAlign: "center" }}>
      <div className="mixed-chart">
        heatmap
          <ReactApexChart
          options={chartOptions}
          series={chartSeries}

        />
      </div>
    </div>

  );
}


export default HeatMap;
