import React from 'react';
import ReactApexChart from "react-apexcharts";
import dataProcess from './data';
const HeatMap = (props) => {

  const [chartOptions, setOptions] = React.useState({});
  const [chartSeries, getSeries] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [customerData, setCustomerData] = React.useState([])
  const dataSeries = [
    {

      data: [{
        x: [customerData.map((item) => item['Location code'])],
        // y: [customerData.map((item) => item['Manual P&A Checks'])]
      }]
    },

  ]

  const dataOptions = {
    chart: {
      toolbar: {
        show: false,
      }
    },
    dataLabels: {
      enabled: true
    },
    colors: ["#2B47D2"]
  };

  React.useEffect(() => {

    setOptions(dataOptions);
    getSeries(dataSeries)
    setLoading(false);

  }, []);

  console.log(dataOptions)
  return (

    <div style={{ padding: "40px" }} >
      <div className="mixed-chart" style={{ width: "60%", margin: "auto" }}>
        <h1>Client Processes</h1>
        <ReactApexChart
          options={chartOptions}
          series={dataSeries}
          type="heatmap"

        />
      </div>
    </div >

  );
}


export default HeatMap;
