import React from 'react';
import ReactApexChart from "react-apexcharts";
import dataProcess from './data';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';

const HeatMap = (props) => {

  const [chartOptions, setOptions] = React.useState({});
  const [chartSeries, getSeries] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [activeQuery, setActiveQuery] = React.useState('Hubs')
  const [customerData, setCustomerData] = React.useState([])
  const [dataByRegion, setDataByRegion] = React.useState([])
  const systems = dataProcess.map((item) => item['Closure Systems'].map((system) => system['System Name']));


  const dataOptions = {
    chart: {
      toolbar: {
        show: false,
      }
    },

    plotOptions: {
      heatmap: {
        radius: 4,
        colorScale: {
          ranges: [{
            from: 0,
            to: 2000,
            color: "#4bb0ff",
            name: "0 - 2000"
          },
          {
            from: 2001,
            to: 4000,
            color: "#0063b0",
            name: "2001 - 4000"
          },
          {
            from: 4001,
            to: 6000,
            color: "#003863",
            name: "4001 - 6000"
          }
          ]
        }

      }
    },
    xaxis: {
      categories: dataByRegion.map((item) => item["Location code"])
    },
    dataLabels: {
      enabled: true,
      // style: {
      //   colors: ["#787878"],
      //   fontWeight: "lighter"
      // },
    },
    title: {
      text: 'Method',
      margin: 10,
      offsetX: 40,
      offsetY: 10,
      floating: true,
      style: {
        fontSize: '16px',

      },

    },

    colors: ["#008FFB"],
  };
  const dataSeries = [{
    name: "Manual Closures",
    data: dataByRegion.map((item) => item["Manual Closures"])
  },
  {
    name: "RPA Closures",
    data: dataByRegion.map((item) => item["RPA Closures"])
  },
  {
    name: "Closures with script available",
    data: dataByRegion.map((item) => item["Closures with script available"])
  }]
  React.useEffect(() => {

    setOptions(dataOptions);
    getSeries(dataSeries)
    setLoading(false);

  }, []);


  React.useEffect(() => {

    const search = (activeQuery, dataProcess) => {

      for (var i = 0; i < dataProcess.length; i++) {

        if (dataProcess[i]['Region'] === activeQuery) {

          setDataByRegion(dataProcess[i]);
        }

      }
    }

    search(activeQuery, dataProcess)
  }, [activeQuery]);
  const handleChange = (e) => {
    setActiveQuery(e.target.value)
  }



  return (

    <div style={{ padding: "40px" }} >
      {loading && <p>Chart loading...</p>}
      <div className="mixed-chart" style={{ margin: "auto" }}>
        <h1>Offboarding Activities</h1>
        <Select
          style={{ width: "100px", textAlign: "center", marginBottom: "40px", marginRight: "20px" }}
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          onChange={handleChange}
          value={activeQuery}


        >
          <MenuItem value={'APAC Branches'}>APAC Branches</MenuItem>
          <MenuItem value={'EMEA Branches'}>EMEA Branches</MenuItem>
          <MenuItem value={'AMER Branches'}>AMER Branches</MenuItem>
          <MenuItem value={'Hubs'}>Hubs</MenuItem>


        </Select>
        <ReactApexChart
          options={chartOptions}
          series={chartSeries}
          type="heatmap"
          height="300px"

        />
        <p>Country Code</p>
      </div>
    </div >

  );
}


export default HeatMap;
