import React from 'react';

import './themes.css'

import Chart from './components/charts/BarChart'

import './App.css';

class App extends React.Component {
  constructor() {
    super()
    this.state = {
      prefersDark: false
    }
  }

  handleClick = () => {

    this.setState(state => ({
      prefersDark: !state.prefersDark
    }));

  }

  render() {

    return (

      <div className={this.state.prefersDark ? 'dark' : 'light'}>

        <Chart handleClick={this.handleClick} />
      </div >

    );
  }

}

export default App;
